<?phpclass ModelExtensionModuleBuyCredit extends Model {	public function install(){        $this->db->query("CREATE TABLE IF NOT EXISTS `" . DB_PREFIX . "order_credit` (          `order_credit_id` int(11) NOT NULL AUTO_INCREMENT,          `order_id` int(11) NOT NULL,          `credit_id` int(11) NOT NULL,          `description` varchar(255) NOT NULL,          `customer_id` int(11) NOT NULL,          `firstname` varchar(32) NOT NULL,          `lastname` varchar(32) NOT NULL,          `email` varchar(96) NOT NULL,          `amount` decimal(15,4) NOT NULL,          `date_added` datetime NOT NULL,          `date_credit` datetime NOT NULL,          PRIMARY KEY (`order_credit_id`)        ) ENGINE=MyISAM COLLATE=utf8_general_ci");	}	public function uninstall(){        $this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "order_credit`");	}} 